export default {
	myVar1: [],
	scores: {
		'player1':0
	},
	incrementPlayer: (pnum, amt) => {
		var key = "player" + pnum + "_score";
		var val = appsmith.store[key] + amt;

		if (val >= 0) { 
			storeValue(key, val);  		
		}
	},
	playerCountChange:() => {
	   for (var x=1; x<=6; x++) {
			 storeValue("player" + x + "_active", x<=parseInt(Select1.selectedOptionValue));
		 }
	},
	initialize: () => {
		this.playerCountChange();
		storeValue('scoreboard','Player 1: 0')
			storeValue("player1_score", 0);  
		storeValue("player2_score", 0);  
		storeValue("player3_score", 0);  
		storeValue("player4_score", 0);  
		storeValue("player5_score", 0);  
		storeValue("player6_score", 0);  
	}
}
